// Get input items (usually one item that wraps the Adzuna API response)
const inputItems = await $input.all();
const rawData = inputItems[0].json;

// Validate structure
if (!Array.isArray(rawData.results || rawData[0]?.results)) {
  throw new Error("Invalid Adzuna API structure");
}

// Extract results array (depending on whether you're flattening results or not)
const jobResults = rawData.results || rawData[0].results;

return jobResults.map(job => ({
  json: {
    id: job.id || null,
    title: job.title || "",
    company: job.company?.display_name || "",
    location: job.location?.display_name || "",
    category: job.category?.label || "",
    description: (job.description || "").replace(/\s+/g, " ").slice(0, 200) + "...",
    url: job.redirect_url || "",
    created: job.created || "",
    latitude: job.latitude || null,
    longitude: job.longitude || null,
    salaryPredicted: job.salary_is_predicted === "1",
  }
}));

// Use a Set to track unique IDs
const seen = new Set();
const deduplicated = [];

for (const item of items) {
  const jobId = item.json.id;

  if (!seen.has(jobId)) {
    seen.add(jobId);
    deduplicated.push(item);
  }
}

// Return only unique job entries
return deduplicated;

